import React, { useState } from 'react';
import { Container, Button, Row, Col, Modal, ModalHeader, ModalBody, ModalFooter, Input, Label, Form, FormGroup } from 'reactstrap';
import Head from 'next/head';
import Menu from '../components/Menu';
import 'bootstrap/dist/css/bootstrap.min.css';

function HomePage() {
    const ModalExample = (props) => {
    const {
        buttonLabel,
        className
      } = props;

      const [modal, setModal] = useState(false);
      const [unmountOnClose, setUnmountOnClose] = useState(true);

      const toggle = () => setModal(!modal);
      const changeUnmountOnClose = e => {
          let value = e.target.value;
          setUnmountOnClose(JSON.parse(value));
      }
    return (
    <div>
                <Head>
                    <title>LinsTur - Controle TVDE</title>
                    <meta name='description' content='Controle de corridas TVDE e Tranfer' />
                    <meta name='author' content='LinsTur' />
                </Head>
                <Menu />

            <Container className="text-center">
                <Row className="valorDia">
                    <style>{`.valorDia{
                                width: 250px;
                                margin: 40px auto;
                                border-radius: 15px;
                                padding-top: 30px;
                                padding-bottom: 30px;
                                background-color: #000;
                                color: #fff;
                                font-size: 24pt;
                            }`}</style>
                <Col sm="12" md={{ size: 6, offset: 3 }}>€ 999</Col>
                </Row>

                <Row>
                    <Col>.col</Col>
                    <Col>.col</Col>
                    <Col>.col</Col>
                    <Col>.col</Col>
                </Row>

            </Container>
            <Button color="primary" size="lg" block>€ Entrada</Button>                    
            </div>
    );
    (
        <div>
            <Form inline onSubmit={(e) => e.preventDefault()}>
                <FormGroup>
                    <Label for="unmountOnClose">UnmountOnClose value</Label>{' '}
                    <Input type="select" name="unmountOnClose" id="unmountOnClose" onChange={changeUnmountOnClose}>
                        <option value="true">true</option>
                        <option value="false">false</option>
                    </Input>
                </FormGroup>
                {' '}
                <Button color="danger" onClick={toggle}>{buttonLabel}</Button>
            </Form>
            <Modal isOpen={modal} toggle={toggle} className={className} unmountOnClose={unmountOnClose}>
                <ModalHeader toggle={toggle}>Modal title</ModalHeader>
                <ModalBody>
                    <Input type="textarea" placeholder="Write something (data should remain in modal if unmountOnClose is set to false)" rows={5} />
                </ModalBody>
                <ModalFooter>
                    <Button color="primary" onClick={toggle}>Do Something</Button>{' '}
                    <Button color="secondary" onClick={toggle}>Cancel</Button>
                </ModalFooter>
            </Modal>
        </div>
    );
  }
}
  export default HomePage
